mapper.select:
select tour_num, tour_name, tour_content, tour_sdate, tour_edate, tour_price, admin_id, term_num
from tours
where tour_num = #{tourNum}

mapper.update:
update tours
set tour_name = #{tourName}, tour_content = #{tourContent}, tour_sdate = #{tourSDate}, tour_edate = #{tourEDate},
	tour_price = #{tourPrice}, admin_id = #{adminId}, term_num = #{termNum}
where tour_num = #{tourNum}
